import React, { useState } from "react"
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';

export default function UseWeatherApi() {
    const [isLoading, setIsLoading] = useState(false)
    const [weather, setWeather] = useState([])
    const [current, setCurrent] = useState([])
    const [location, setLocation] = useState([])

    const HandleClick = async () => {
        

        const response = await fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=current&key=JB7CB9FV8WLE3SE7N3AYLFWT9&contentType=json`, {
            "method": "GET",
            "headers": {
                'Content-Type': 'application/json'
            }
            })
            
        .then(response => {
            response.json()
        })
        .then(data => {
            setWeather(data);
            setCurrent(data.currentConditions);
        })
        .catch(error => console.log(error));
    }

    
        
        
        
    
      
    
    console.log(weather)
    console.log(current)

    return(
        <div>
            
            <DropdownButton id="dropdown-basic-button" title="Dropdown button">
                <Dropdown.Item href="#/action-1" onClick={setLocation('melbourne')}>Melbourne</Dropdown.Item>
                <Dropdown.Item href="#/action-2" onClick={setLocation('sydney')}>Sydney</Dropdown.Item>
            </DropdownButton>
            <Button variant="primary" onClick={HandleClick()}>Submit</Button>

        </div>
    )
}
