import React, { useEffect, useState } from "react"
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import 'bootstrap/dist/css/bootstrap.min.css';

export default function UseWeatherApi() {
    const [weather, setWeather] = useState([])
    const [current, setCurrent] = useState([])



    function HandleClick(location) {
        return function() {
                let FetchWeather = () => {
        fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=current&key=JB7CB9FV8WLE3SE7N3AYLFWT9&contentType=json`, {
            "method": "GET",
            "headers": {
                'Content-Type': 'application/json'
            }
            })
        .then(response => {
            return response.json()
        })
        .then(data => {
            setWeather(data);
            setCurrent(data.currentConditions);
        })
        .catch(error => console.log(error));




        useEffect(() => {
            FetchWeather()
        }, [])
        
    }
        }
    }
    console.log(weather)
    console.log(current)

    return(
        <div>
            <p>{weather.resolvedAddress}</p>
            <p>{current.temp} degrees</p>
            <DropdownButton id="dropdown-basic-button" title="Dropdown button">
                <Dropdown.Item href="#/action-1" onClick={HandleClick('melbourne')}>Melbourne</Dropdown.Item>
                <Dropdown.Item href="#/action-2" onClick={HandleClick('sydney')}>Sydney</Dropdown.Item>
            </DropdownButton>
        </div>
    )
}
