import React, { useState } from "react";
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function UseWeatherAPI() {
    const [location, setLocation] = useState('Location');
    const [weather, setWeather] = useState([]);
    const [days, setDays] = useState([]);
    
    
    const WeatherGetAPI = () => {
        if (location !== "Location"){
              fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=days&key=S7J8YKHYZTCWAAE9T4BTR9D4K&contentType=json`, {
                "method": "GET",
                "headers": {
                    'Content-Type': 'application/json'
                }
            })
            .then(response => {
                return response.json();
            })
            .then(data => {
                setWeather(data);
                setDays(data.days[0]);
                console.log(data);
            })
            .catch(error => console.log(error))
            
        }else{
            console.log(location)
        };
    };    
          
    const HandleClick = (x) => {
        setLocation(x);
        console.log(x)
        console.log(weather)
        console.log(days)

    };
    
        return(
            <main>
                <div id="buttons-container">
                    <DropdownButton id="dropdown-basic-button" title={location}>
                        <Dropdown.Item onClick={() => {HandleClick('Melbourne')}}>Melbourne</Dropdown.Item>
                        <Dropdown.Item onClick={() => {HandleClick('Sydney')}} >Sydney</Dropdown.Item>
                    </DropdownButton>
                    <Button variant="primary" onClick={() => {WeatherGetAPI()}}>Submit</Button>{' '}
                </div>
                <div id="weather-container">
                    <h2>{weather.resolvedAddress}</h2>
                    <ul>
                        <li>{days.datetime}</li>
                        <li>Temperature Avg {days.temp}</li>
                        <li>Temperature Max {days.tempmax}</li>
                        <li>Temperature Min {days.tempmin}</li>
                    </ul>
                </div>
            </main>
        );
    
};
