import React from "react"
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';

class UseWeatherAPI extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            location: "Location",
            weather: []
        };
    };
    
    weatherGetAPI = () => {
        if (this.state.location != "Location"){
              fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${this.state.location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=days&key=S7J8YKHYZTCWAAE9T4BTR9D4K&contentType=json`, {
                "method": "GET",
                "headers": {
                    'Content-Type': 'application/json'
                }
            })
            .then(response => {
                return response.json()
            })
            .then(data => {
                this.setState({weather: data})
                console.log(data)
            })
            .catch(error => console.log(error));
        }else{
            console.log(this.state.location)
        };
    }    
          
    handleClick = (x) => {
        this.setState({location: x})
        console.log(x)
        console.log(this.state.weather)
    }
    
    render(){
        return(
            <div>
                <DropdownButton id="dropdown-basic-button" title={this.state.location}>
                    <Dropdown.Item onClick={() => {this.handleClick('Melbourne')}}>Melbourne</Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.handleClick('Sydney')}} >Sydney</Dropdown.Item>
                </DropdownButton>
                <Button variant="primary" onClick={() => {this.weatherGetAPI()}}>Submit</Button>{' '}
            </div>
        )
    }
}       
       

export default UseWeatherAPI;
