import React , {useState} from "react"
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';

class UseWeatherAPI extends React.Component {
    constructor(props){
        super(props);
        this.state = {location: "melbourne"};
    };
    

        FetchAPI = () => {
            fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${this.state.location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=current&key=JB7CB9FV8WLE3SE7N3AYLFWT9&contentType=json`, {
                "method": "GET",
                "headers": {
                    'Content-Type': 'application/json'
                }
                })
                
            .then(response => {
                return response.json()
            })
            .then(data => {
            
                console.log(data)
            
            })
            .catch(error => console.log(error));
        }

    
    selectLocation = (x) => {
        this.setState.location(x)
    }

    render() {
        return(
            <div>
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Location
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item href="#/action-melb" >Melbourne</Dropdown.Item>
                        <Dropdown.Item href="#/action-sydn">Sydney</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            </div>
        )

    }
       
       
}
export default UseWeatherAPI;
