import React, { useState } from "react"
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';

export default function UseWeatherAPI() {
    const [location, setLocation] = useState()
    const [weather, setWeather] = useState([])
    const [days, setDays] = useState([])
        // this.state = {
        //     location: "Location",
        //     weather: [],
        //     days: []
        // };
    
    
    const WeatherGetAPI = () => {
        if (this.state.location != "Location"){
              fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${this.state.location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=days&key=S7J8YKHYZTCWAAE9T4BTR9D4K&contentType=json`, {
                "method": "GET",
                "headers": {
                    'Content-Type': 'application/json'
                }
            })
            .then(response => {
                return response.json()
            })
            .then(data => {
                this.setState({weather: data})
                this.setState({days: data.days[0]})
                console.log(data)
            })
            .catch(error => console.log(error));
        }else{
            console.log(this.state.location)
        };
    }    
          
    const HandleClick = (x) => {
        this.setState({location: x})
        console.log(x)
        console.log(this.state.weather)
        console.log(this.state.days)
    }
    
        return(
            <div>
                <div id="buttons-container">
                    <DropdownButton id="dropdown-basic-button" title={this.state.location}>
                        <Dropdown.Item onClick={() => {this.HandleClick('Melbourne')}}>Melbourne</Dropdown.Item>
                        <Dropdown.Item onClick={() => {this.HandleClick('Sydney')}} >Sydney</Dropdown.Item>
                    </DropdownButton>
                    <Button variant="primary" onClick={() => {this.WeatherGetAPI()}}>Submit</Button>{' '}
                </div>
                <div id="weather-container">
                    <h2>{this.state.weather.resolvedAddress}</h2>
                    <ul>
                        <li>{this.state.days.datetime}</li>
                        <li>Temperature Avg {this.state.days.temp}</li>
                        <li>Temperature Max {this.state.days.tempmax}</li>
                        <li>Temperature Min {this.state.days.tempmin}</li>
                    </ul>
                </div>
            </div>
        )
    
}
