import React from "react"
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'
import 'bootstrap/dist/css/bootstrap.min.css';

class UseWeatherAPI extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            location: "melbourne",
            weather: []
        };
    };
    

        componentDidMount(){
            fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${this.state.location}?unitGroup=metric&elements=datetime%2Ctempmax%2Ctempmin%2Ctemp&include=current&key=JB7CB9FV8WLE3SE7N3AYLFWT9&contentType=json`, {
                "method": "GET",
                "headers": {
                    'Content-Type': 'application/json'
                }
                })
                
            .then(response => {
                response.json()
            })
            .then(data => {
                this.setState({weather: data})
                console.log(data)
            
            })
            .catch(error => console.log(error));
        };
    
    handleClick = async (x) => {
            this.setState({location: x})
            console.log(x)
        }
    render(){
        return(
            <div>
                <DropdownButton id="dropdown-basic-button" title="Dropdown button">
                    <Dropdown.Item onClick={this.handleClick('melbourne')}>Melbourne</Dropdown.Item>
                    <Dropdown.Item onClick={this.handleClick('sydney')} >Sydney</Dropdown.Item>
                </DropdownButton>
            </div>
        )

    }
}       
       

export default UseWeatherAPI;
