import React, { useEffect, useState } from "react"
import CitySelector from "./citySelect.js"

export default function UseWeatherApi() {
    const [weather, setWeather] = useState([])
    const [current, setCurrent] = useState([])

    let FetchWeather = () => {
        fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?unitGroup=metric&include=current&key=JB7CB9FV8WLE3SE7N3AYLFWT9&contentType=json`, {
            "method": "GET",
            "headers": {
                'Content-Type': 'application/json'
            }
            })
        .then(response => {
            return response.json()
        })
        .then(data => {
            setWeather(data);
            setCurrent(data.currentConditions);
        })
        .catch(error => console.log(error));

        useEffect(() => {
            FetchWeather()
        }, [])
    }

    
    console.log(weather)
    console.log(current)

    return(
        <div>
            <p>{weather.resolvedAddress}</p>
            <p>{current.temp} degrees</p>
            <CitySelector />
        </div>
    )
}
